com.mirth.connect.plugins.javascriptstep.script:
// Extract data from HL7v2 message
var hl7Message = msg;
logger.info('Incoming HL7: ' + hl7Message);

var patientIdValue = hl7Message['PID']['PID.3']['PID.3.1'].toString();
var patientIdSystem = hl7Message['PID']['PID.3']['PID.3.4'].toString();
var patientFamilyName = hl7Message['PID']['PID.5']['PID.5.1'].toString();
var patientGivenName = hl7Message['PID']['PID.5']['PID.5.2'].toString();
var genderRaw = hl7Message['PID']['PID.8']['PID.8.1'].toString();
var birthdateRaw = hl7Message['PID']['PID.7']['PID.7.1'].toString();

var encounterStatusRaw = hl7Message['PV1']['PV1.2']['PV1.2.1'].toString(); 
var encounterIdValue = hl7Message['PV1']['PV1.19']['PV1.19.1'].toString();
var encounterIdSystem = hl7Message['PV1']['PV1.19']['PV1.19.4'].toString();

var practitionerIdValue = hl7Message['PV1']['PV1.7']['PV1.7.1'].toString();
var practitionerIdSystem = hl7Message['PV1']['PV1.7']['PV1.7.14'].toString();
var practitionerFamilyName = hl7Message['PV1']['PV1.7']['PV1.7.2'].toString();
var practitionerGivenName = hl7Message['PV1']['PV1.7']['PV1.7.3'].toString();
var practitionerPrefix = hl7Message['PV1']['PV1.7']['PV1.7.6'].toString(); 

// Create dictionary with the extracted values
var logData = {
    patientIdValue: patientIdValue,
    patientIdSystem: patientIdSystem,
    patientFamilyName: patientFamilyName,
    patientGivenName: patientGivenName,
    genderRaw: genderRaw,
    birthdateRaw: birthdateRaw,
    encounterStatusRaw: encounterStatusRaw,
    encounterIdValue: encounterIdValue,
    encounterIdSystem: encounterIdSystem,
    practitionerIdValue: practitionerIdValue,
    practitionerIdSystem: practitionerIdSystem,
    practitionerFamilyName: practitionerFamilyName,
    practitionerGivenName: practitionerGivenName,
    practitionerPrefix: practitionerPrefix
};

// Log the dictionary
logger.info('Extracted Data: ' + JSON.stringify(logData));

// Create Patient Resource
var patient = {
    "resourceType": "Patient"
};
if (patientIdSystem && patientIdValue) {
    patient.identifier = [{
        "value": patientIdValue,
        "system": patientIdSystem
    }];
}
else {
	logger.warn("Patient has no identifier (no value and/or system)");
}
if (patientFamilyName) {
    patient.name = patient.name || [{}];
    patient.name[0].family = patientFamilyName;
}
if (patientGivenName) {
    patient.name = patient.name || [{}];
    patient.name[0].given = [patientGivenName];
}
if (genderRaw) {
	// Map HL7v2 gender codes to FHIR gender values
	var genderMap = {
	    "M": "male",
	    "F": "female",
	    "O": "other",
	    "U": "unknown"
	};
    patient.gender = genderMap[genderRaw];
}
if (birthdateRaw) {
    patient.birthDate = birthdateRaw.substring(0, 4) + "-" + birthdateRaw.substring(4, 6) + "-" + birthdateRaw.substring(6, 8);
}
var patientUuid = "urn:uuid:" + UUIDGenerator.getUUID();

// Create Encounter Resource
var encounter = {
    "resourceType": "Encounter",
    "identifier": [{
        "value": encounterIdValue,
        "system": encounterIdSystem
    }],
}
if (encounterIdSystem && encounterIdValue) {
    encounter.identifier = [{
        "value": encounterIdValue,
        "system": encounterIdSystem
    }];
}
else {
	logger.warn("Encounter has no identifier (no value and/or system)");
}
if (encounterStatusRaw) {
	// Map HL7v2 Encounter Status
	// https://build.fhir.org/ig/HL7/v2-to-fhir/ConceptMap-table-hl70004-to-encounter-status.html
	var encounterStatusMap = {
	    "E": "in-progress",
	    "I": "in-progress",
	    "O": "in-progress",
	    "P": "planned",
	    "R": "in-progress",
	    "B": "in-progress",
	    "C": "in-progress",
	    "N": "in-progress",
	    "U": "unknown"
	};
	encounter.status = encounterStatusMap[encounterStatusRaw];
}
if (patientUuid) {
	encounter.subject = patientUuid;
} else {
	logger.error("Encounter has no subject");
}
var encounterUuid = "urn:uuid:" + UUIDGenerator.getUUID();

// Create Practitioner Resource
var practitioner = {
    "resourceType": "Practitioner",
};
if (patientIdSystem && patientIdValue) {
    patient.identifier = [{
        "value": patientIdValue,
        "system": patientIdSystem
    }];
}
else {
	logger.warn("Practitioner has no identifier (no value and/or system)");
}
if (practitionerFamilyName) {
    practitioner.name = practitioner.name || [{}];
    practitioner.name[0].family = practitionerFamilyName;
}
if (practitionerGivenName) {
    practitioner.name = practitioner.name || [{}];
    practitionerent.name[0].given = [practitionerGivenName];
}
if (practitionerPrefix) {
    practitioner.prefix = practitioner.name || [{}];
    practitionerent.prefix[0].given = [practitionerPrefix];
}
var practitionerUuid = "urn:uuid:" + UUIDGenerator.getUUID();

// Create FHIR Bundle
var bundle = {
    "resourceType": "Bundle",
    "type": "transaction",
    "entry": []
};

if (patientIdSystem && patientIdValue) {
    bundle.entry.push({
        "resource": patient,
        "fullUrl": patientUuid,
        "request": {
            "method": "PUT",
            "url": "Patient?identifier=" + encodeURIComponent(patientIdSystem + "|" + patientIdValue)
        }
    });
}

if (encounterIdSystem && encounterIdValue) {
    bundle.entry.push({
        "resource": encounter,
        "fullUrl": encounterUuid,
        "request": {
            "method": "PUT",
            "url": "Encounter?identifier=" + encodeURIComponent(encounterIdSystem + "|" + encounterIdValue)
        }
    });
}

// Conditional update for practitioner is not yet supported...
// if (practitionerIdSystem && practitionerIdValue) {
//     bundle.entry.push({
//         "resource": practitioner,
//         "fullUrl": practitionerUuid,
//         "request": {
//             "method": "PUT",
//             "url": "Practitioner?identifier=" + encodeURIComponent(practitionerIdSystem + "|" + practitionerIdValue)
//         }
//     });

// Convert bundle to JSON
var jsonString = JSON.stringify(bundle);
logger.info('Outgoing FHIR: ' + jsonString);
// Set the transformed message to the JSON string
msg = jsonString;

channel.postprocessingScript:
// This script executes once after a message has been processed
// Responses returned from here will be stored as "Postprocessor" in the response map
return;

channel.deployScript:
// This script executes once when the channel is deployed
// You only have access to the globalMap and globalChannelMap here to persist data
return;

