try {
	parameters = sourceMap.get("parameters");
	if (parameters !== null) {
	    patientId = parameters.getParameter("patient");
	    patientGender = parameters.getParameter("sex");
	    patientBirthDate = parameters.getParameter("dob");
	    encounterId = parameters.getParameter("encounter");
	    user = parameters.getParameter("user");
	    supervisor = parameters.getParameter("supervisor");
	} else {
	    logger.error("Parameters object is null.");
	}

	// validate query params
	if (patientId !== null) {
	   logger.info("Parameter 'patient_id' is set, value: " + patientId);
	} else {
	   logger.warn("Parameter 'patient_id' is NOT set.");
	}
	if (encounterId !== null) {
	   logger.info("Parameter 'encounter_id' is set, value: " + encounterId);
	} else {
	   logger.info("Parameter 'encounter_id' is NOT set.");
	}

	// Patient Information
	channelMap.put("patientIdValue", patientId);
	channelMap.put("patientIdSystem", "https://fhir.nexuzhealth.com/standards/fhir/NamingSystem/kws/pm/patient/eadnr");
	// channelMap.put("patientFamilyName", patientFamilyName); // Optional: Patient's family/last name
	// channelMap.put("patientGivenName", patientGivenName);   // Optional: Patient's given/first name
	channelMap.put("patientGender", patientGender);         // Optional: Patient's gender (e.g., "male", "female")
	channelMap.put("patientBirthDate", patientBirthDate);   // Optional: Patient's birth date (YYYY-MM-DD)

	// Encounter Information
	channelMap.put("encounterIdValue", encounterId);
	channelMap.put("encounterIdSystem", "https://fhir.nexuzhealth.com/standards/fhir/NamingSystem/kws/bifrost/receptie/cnr");
	channelMap.put("encounterStatus", "unknown"); //  Encounter's status (e.g., "planned", "in-progress")
	channelMap.put("encounterRespIdSystem", "https://tiro.health/standards/fhir/NamingSystem/kws/user-id"); // Optional, responsible identifier for the encounter 
	channelMap.put("encounterRespIdValue", supervisor); // Optional, responsible identifier for the encounter 
	// Practitioner Information
	// channelMap.put("practitionerIdValue", practitionerIdValue);   // Optional: Practitioner identifier value
	// channelMap.put("practitionerIdSystem", "http://launch.com");  // Optional: Practitioner identifier system
	// channelMap.put("practitionerFamilyName", practitionerFamilyName); // Optional: Practitioner's family/last name
	// channelMap.put("practitionerGivenName", practitionerGivenName);   // Optional: Practitioner's given/first name
	// channelMap.put("practitionerPrefix", practitionerPrefix);         // Optional: Practitioner's prefix (e.g., "Dr.", "Mr.")

} catch (error) {
    // Log any errors for debugging purposes
    logger.error("Error while parsing the URL: " + error.message);
    throw error; // Re-throw the error to handle it upstream if needed
}

try {

	logger.info("destination tranformer");

	logger.info("Creating data import transaction bundle");

	// Create Patient Resource
	var patient = {
	    "resourceType": "Patient"
	};
	patientIdSystem = channelMap.get("patientIdSystem");
	patientIdValue = channelMap.get("patientIdValue");
	if (patientIdSystem && patientIdValue) {
	    patient.identifier = [{
	        "value": patientIdValue,
	        "system": patientIdSystem
	    }];
	} else {
	    logger.warn("Patient has no identifier (no value and/or system)");
	}
	patientFamilyName = channelMap.get("patientFamilyName");
	patientGivenName = channelMap.get("patientGivenName");
	if (patientFamilyName || patientGivenName) {
	    patient.name = [{}]; // Initialize as an array with one empty object
	    if (patientFamilyName) {
	        patient.name[0].family = patientFamilyName;
	    }
	    if (patientGivenName) {
	        patient.name[0].given = [patientGivenName];
	    }
	}
	patientGender = channelMap.get("patientGender");
	if (patientGender) {
	    patient.gender = patientGender;
	}
	patientBirthDate = channelMap.get("patientBirthDate");
	if (patientBirthDate) {
	    patient.birthDate = patientBirthDate;
	}
	var patientUuid = "urn:uuid:" + UUIDGenerator.getUUID();
	logger.info("Handled patient resource");

	// Create Encounter Resource
	var encounter = {
	    "resourceType": "Encounter"
	};
	encounterIdValue = channelMap.get("encounterIdValue");
	encounterIdSystem = channelMap.get("encounterIdSystem");
	if (encounterIdSystem && encounterIdValue) {
	    encounter.identifier = [{
	        "value": encounterIdValue,
	        "system": encounterIdSystem
	    }];
	} else {
	    logger.warn("Encounter has no identifier (no value and/or system)");
	}
	var encounterRespIdSystem = channelMap.get("encounterRespIdSystem");
	var encounterRespIdValue = channelMap.get("encounterRespIdValue");
	if (encounterRespIdSystem && encounterRespIdValue) {
		var responsibleUuid = "urn:uuid:" + UUIDGenerator.getUUID();
		encounter.participant = [
		    {
		      "type": [
		        {
		          "coding": [
		            {
		              "system": "http://terminology.hl7.org/CodeSystem/v3-ParticipationType",
		              "code": "RESP",
		              "display": "responsible party"
		            }
		          ]
		        }
		      ],
		      "actor": {
		        "identifier": {
		        	"system": encounterRespIdSystem,
		        	"value": encounterRespIdValue
		        	},
		      }
		    }
		  ];
	}

	var encounterStatus = channelMap.get("encounterStatus");
	if (encounterStatus) {
	    encounter.status = encounterStatus;
	}
	if (patientUuid) {
	    encounter.subject = {
	        "reference": patientUuid
	    };
	} else {
	    logger.error("Encounter has no subject");
	}
	var encounterUuid = "urn:uuid:" + UUIDGenerator.getUUID();
	logger.info('Handled encounter resource');

	// Create Practitioner Resource
	var practitioner = {
	    "resourceType": "Practitioner",
	};
	practitionerIdValue = channelMap.get("practitionerIdValue");
	practitionerIdSystem = channelMap.get("practitionerIdSystem");
	if (practitionerIdSystem && practitionerIdValue) {
	    practitioner.identifier = [{
	        "value": practitionerIdValue,
	        "system": practitionerIdSystem
	    }];
	} else {
	    logger.warn("Practitioner has no identifier (no value and/or system)");
	}
	practitionerFamilyName = channelMap.get("practitionerFamilyName");
	practitionerGivenName = channelMap.get("practitionerGivenName");
	practitionerPrefix = channelMap.get("practitionerPrefix");
	if (practitionerFamilyName || practitionerGivenName || practitionerPrefix) {
	    practitioner.name = [{}]; // Initialize as an array with one empty object
	    if (practitionerFamilyName) {
	        practitioner.name[0].family = practitionerFamilyName;
	    }
	    if (practitionerGivenName) {
	        practitioner.name[0].given = [practitionerGivenName];
	    }
	    if (practitionerPrefix) {
	        practitioner.name[0].prefix = [practitionerPrefix];
	    }
	}
	var practitionerUuid = "urn:uuid:" + UUIDGenerator.getUUID();
	logger.info('Handled practitioner resource');

	// Create FHIR Bundle
	var bundle = {
	    "resourceType": "Bundle",
	    "type": "transaction",
	    "entry": []
	};

	if (patientIdSystem && patientIdValue) {
	    bundle.entry.push({
	        "resource": patient,
	        "fullUrl": patientUuid,
	        "request": {
	            "method": "PUT",
	            "url": "Patient?identifier=" + encodeURIComponent(patientIdSystem + "|" + patientIdValue)
	        }
	    });
	}

	if (encounterIdSystem && encounterIdValue) {
	    bundle.entry.push({
	        "resource": encounter,
	        "fullUrl": encounterUuid,
	        "request": {
	            "method": "PUT",
	            "url": "Encounter?identifier=" + encodeURIComponent(encounterIdSystem + "|" + encounterIdValue)
	        }
	    });
	}

	if (practitionerIdSystem && practitionerIdValue) {
	    bundle.entry.push({
	        "resource": practitioner,
	        "fullUrl": practitionerUuid,
	        "request": {
	            "method": "PUT",
	            "url": "Practitioner?identifier=" + encodeURIComponent(practitionerIdSystem + "|" + practitionerIdValue)
	        }
	    });
	}

	// Convert bundle to JSON
	var jsonString = JSON.stringify(bundle);

	logger.info("transactionBundle: " + jsonString)
	channelMap.put("transactionBundle", jsonString);

} catch (error) {
    // Log any errors for debugging purposes
    logger.error("Error while parsing the URL: " + error.message);
    throw error; // Re-throw the error to handle it upstream if needed
}

logger.info("In response transformer");
try{
	// Capture the HTTP response status code
	var statusCode = $('responseStatusCode');
	var statusMessage = $('responseStatusLine');
     var responseBody = response.getMessage();

	// Check if the status code is in the 2xx range
	if(statusCode=="200"||statusCode == "201") {
		
		// get the subject id, so the user does not need to select the subject
		var fhirBundle = JSON.parse(responseBody);		var patientId = null;
		if (fhirBundle && fhirBundle.entry && Array.isArray(fhirBundle.entry)) {
		    for (var i = 0; i < fhirBundle.entry.length; i++) {
		        var entry = fhirBundle.entry[i];
		        if (entry.resource && entry.resource.resourceType === 'Patient') {
		            patientId = entry.resource.id;
		            logger.info("Patient id found");
		            break;
		        }
		    }
		}    		channelMap.put("redirect", "https://app.tiro.health/reports?subject=Patient/" + patientId);
	    logger.info("Status is 2xx. Redirect URL set to app.tiro.health");
	} else {
	    var error = "We encountered an integration error. Please contact your IT department for assistance.";

	    var detailedError = "Error in MIRTH channel:\n" + 
	    		"name: " + channelName + "\n" +
	    		"id: " + channelId + "\n" +
	    		"statusCode: " + statusCode + "\n" +
	    		"statusMessage: " + statusMessage + "\n" +
	    		"responseBody: " + responseBody + "\n";
	    channelMap.put("redirect", "https://reports.tiro.health/ui/error?error=" + encodeURIComponent(error) + "&detailed_error=" + encodeURIComponent(detailedError));
	    logger.info("Status is NOT 2xx. Redirect URL set to an error screen.");
	}
	responseMap.put("statusCode", "307");
} catch (error) {
    logger.error("Error occurred: " + error.message);
    throw error; // Pass the error upstream
}

